import React, { Component } from 'react';

import {authAxios} from '../store/utility';
import {
    Button,
    Container,
    Divider,
    Header,
    Modal,
    Icon,
    Input,
    Table
  } from "semantic-ui-react"

import {employeesListURL,
emploryeeURL,
employeeCreateURL,
employeeUpdateURL,
mployeeDeleteURL} from '../constants';


class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                employees: [],
                error: null,
                loading: false,
                firstopen: false,
                secondopen: false
        }
        // this.addEmployee = this.addEmployee.bind(this);
        // this.editEmployee = this.editEmployee.bind(this);
        // this.deleteEmployee = this.deleteEmployee.bind(this);
    }

    componentDidMount(){
        authAxios
        .get(employeesListURL, {
            headers:{
                'Authorization' : 'Bearer '+localStorage.getItem('token')
            }
        })
        .then(res => {
          this.setState({ employees: res.data.employees, loading: false });
        })
    }

    setFirstOpen = () =>{
        this.setState({ firstopen: !this.state.firstopen });
      };

    setSecondOpen = () =>{
        this.setState({ secondopen: !this.state.secondopen });
    };


    render() {
        console.log('employees',this.state.employees)
        const {employees,firstopen,secondopen} =this.state
        return (
            <Container>
                <Header as='h2'>Employees List</Header>
                <Container textAlign='right'>
                    <Button content='Add Employee' primary onClick={this.setFirstOpen}/>
                    <Modal
                        onClose={ this.setFirstOpen}
                        onOpen={ this.setFirstOpen}
                        open={firstopen}
                    >
                        <Modal.Header>Enter Employee Details</Modal.Header>
                        <Modal.Content>
                            <Input fluid icon='user' placeholder='Enter First Name' />
                            <Divider/>
                            <Input fluid icon='user' placeholder='Enter Last Name' />
                            <Divider/>
                            <Input fluid icon='mail' placeholder='Enter E-Mail' />
                            <Divider/>
                            <Input fluid icon='home' placeholder='Enter Address' />
                            <Divider/>
                            <Input fluid icon='calendar' placeholder='Enter Date of Birth' />
                            <Divider/>
                            <Input fluid icon='industry' placeholder='Enter Comapny Name' />
                            <Divider/>
                            <Input fluid icon='phone' placeholder='Enter Mobile No' />
                            <Divider/>
                            <Input fluid icon='building' placeholder='Enter City' />
                        </Modal.Content>
                        <Modal.Actions>
                        <Button onClick={ this.setSecondOpen } primary>
                            Proceed <Icon name='right chevron' />
                        </Button>
                        </Modal.Actions>

                        <Modal
                        onClose={ this.setSecondOpen }
                        open={secondopen}
                        size='small'
                        >
                        <Modal.Header>Are you sure!</Modal.Header>
                        <Modal.Content>
                            <p>Are you sure that you want to save these employee details</p>
                        </Modal.Content>
                        <Modal.Actions>
                            <Button
                            icon='check'
                            content='All Done'
                            onClick={ this.setSecondOpen}
                            />
                        </Modal.Actions>
                        </Modal>
                    </Modal>
                </Container>
                <Divider />
                <Container>
                <Table singleLine>
                    <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Id</Table.HeaderCell>
                        <Table.HeaderCell>First Name</Table.HeaderCell>
                        <Table.HeaderCell>Last Name</Table.HeaderCell>
                        <Table.HeaderCell>E-mail</Table.HeaderCell>
                        <Table.HeaderCell>Mob No.</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {
                            employees.map((employee,index) => { return(
                                <Table.Row key={index}>
                                    <Table.Cell>{employee.id}</Table.Cell>
                                    <Table.Cell>{employee.firstname}</Table.Cell>
                                    <Table.Cell>{employee.lastname}</Table.Cell>
                                    <Table.Cell>{employee.email}</Table.Cell>
                                    <Table.Cell>{employee.mobile}</Table.Cell>
                                    <Table.Cell>
                                        <Button content='Edit'/>

                                        <Button content='Remove' />
                                    </Table.Cell>
                                </Table.Row>
                            )
                            })

                        }
                    </Table.Body>
                </Table>
                </Container>
            </Container>
        )
    }
}

export default ListEmployeeComponent ;
