import React, { Component } from 'react';

import {authAxios} from '../store/utility';
import {
    Button,
    Container,
    Header,
    Table
  } from "semantic-ui-react"

import {employeesListURL,
emploryeeURL,
employeeCreateURL,
employeeUpdateURL,
mployeeDeleteURL} from '../constants';


class ListEmployeeComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                employees: [],
                error: null,
                loading: false
        }
        // this.addEmployee = this.addEmployee.bind(this);
        // this.editEmployee = this.editEmployee.bind(this);
        // this.deleteEmployee = this.deleteEmployee.bind(this);
    }

    componentDidMount(){
        authAxios
        .get(employeesListURL, {
            headers:{
                'Authorization' : 'Bearer '+localStorage.getItem('token')
            }
        })
        .then(res => {
          this.setState({ employees: res.data.employees, loading: false });
        })
    }



    render() {
        console.log('employees',this.state.employees)
        const {employees} =this.state
        return (
            <Container>
                 <Header as='h2'>Employees List</Header>
                 <Container textAlign='right'>
                    <Button content='Add Employee' primary/>
                 </Container>

                 <Container>
                <Table singleLine>
                    <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Id</Table.HeaderCell>
                        <Table.HeaderCell>First Name</Table.HeaderCell>
                        <Table.HeaderCell>Last Name</Table.HeaderCell>
                        <Table.HeaderCell>E-mail</Table.HeaderCell>
                        <Table.HeaderCell>Mob No.</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {
                            employees.map(employee =>{
                                <Table.Row key={employee.id}>
                                    <Table.Cell>{employee.id}</Table.Cell>
                                    <Table.Cell>{emmployee.firstname}</Table.Cell>
                                    <Table.Cell>{employee.lastname}</Table.Cell>
                                    <Table.Cell>{employee.email}</Table.Cell>
                                    <Table.Cell>{employee.mobile}</Table.Cell>
                                    <Table.Cell>
                                        <Button/>
                                        <Button/>
                                    </Table.Cell>
                                </Table.Row>
                            })

                        }
                    </Table.Body>
                </Table>

                 </Container>

            </Container>
        )
    }
}

export default ListEmployeeComponent ;
